import sqlite3
con = sqlite3.connect('ccpr.db')
con.execute("CREATE TABLE Volunteer (id integer primary key, volunteerName varchar(15) not null, volunteerLastName varchar(15) not null, volunteerAddress varchar(30) not null, volunteerCity varchar(10) not null, volunteerZipCode varchar(5) not null, volunteerChurch text not null, volunteerGender varchar(1) not null, volunteerPhone varchar(10) not null, volunteerOccupation text, volunteerChaplain varchar(5) not null, volunteerInterest text, volunteerZone int(1) not null)")
con.execute("CREATE TABLE Prison (id integer primary key, prisonName varchar(20) not null, prisonAddress varchar(30), prisonCity varchar(10) not null, prisonZipCode int(6) not null, prisonGender varchar(1) not null)")
con.execute("CREATE TABLE Prisoner(id integer primary key, prisonerName varchar(20) not null, prisonerGender varchar(1) not null, prisonerZone varchar(1), prisonID int not null, volunteerID int not null, foreign key (prisonID) references Prison(id) on update cascade, foreign key (volunteerID) references Volunteer(id) on update cascade)")
con.execute("CREATE TABLE Visit(id integer primary key, volunteerID int references Volunteer(id) on DELETE CASCADE, prisonID int references Prison(id) on update cascade, visitDay varchar(10) not null, visitHour varchar(8) not null, visitProgram text not null)")
con.execute("CREATE TABLE Period(id integer primary key, periodInitial varchar(10) not null, periodLast varchar(10))")
con.execute("CREATE TABLE Child(id integer primary key, prisonerID int references Prisoner(id) on delete set null, childName varchar(15), childLastName varchar(15), childAge int not null, childGender text not null, childAddress text not null)")
con.execute("CREATE TABLE Position(id integer primary key, positionName varchar(15) not null, posDescription text, posTerms int(1) not null)")
con.execute("CREATE TABLE Board(id integer primary key, periodID int, volunteerID int, positionID int, foreign key (positionID) references `Position`(id) on update cascade, foreign key (volunteerID) references Volunteer(id) on update cascade, foreign key (periodID) references Period(id) on update cascade)")
